import React from 'react';
import {useSelector , useDispatch } from 'react-redux';
import Operations from '../redux/action';
import SignUpPage from '../redux/action'
import {useState } from 'react';
import Alert from 'react-popup-alert'

function Login() {
  const [ logindata,changedata] = useState({
      email : ' ',
      password : ' ',
      operation : 'signup'

  })
  function submitclick()
  {
      console.log(logindata.email);
    changedata({
      ...logindata ,
        operation : 'loginSubmit'
    })
    var userData = {
      'email' : logindata.email,
      'password' : logindata.password,
      'operation' : 'loginSubmit'
    }
    dispatch(Operations(userData));

  }
  function signupPageClick()
  {
    changedata({
      ...logindata ,
        operation : 'signup'
    })
    var userData = {
      'email' : logindata.email,
      'password' : logindata.password,
      'operation' : 'signup'
    }
    dispatch(Operations(userData));

  }
  function handleChange(e)
  {
      console.log(e.target.value);
      console.log(logindata);
      if(e.target.id ==='x1')
      {
         changedata(
             {
            ...logindata ,
            email : e.target.value
             }
         ) ;


      }
      else if(e.target.id === 'x2')
      {
        changedata(
            {
           ...logindata ,
           password : e.target.value
            }
        ) ;
      }
  }
  const email = useSelector(state=>state.email);
  const password = useSelector(state=>state.password);
  const component = useSelector(state=>state.component);


  console.log(email,password , component);
  const dispatch = useDispatch()
  console.log("final" , email,password);
  return (


    <div class="wrapper App">
        <div class="logo">
            <img src="https://www.freepnglogos.com/uploads/twitter-logo-png/twitter-bird-symbols-png-logo-0.png" alt=""/>
        </div>
        <div class="text-center mt-4 name">
            Twitter
        </div>
        <form class="p-3 mt-3">
            <div class="form-field d-flex align-items-center">
                <span class="far fa-user"></span>
                <input type="text" name="userName" id="userName" placeholder="Username" onChange={handleChange}/>
            </div>
            <div class="form-field d-flex align-items-center">
                <span class="fas fa-key"></span>
                <input type="password" name="password" id="password" placeholder="Password" onChange={handleChange}/>
            </div>
            <button class="btn mt-3" onClick={()=>submitclick()}>Login</button>
        </form>
        <div class="text-center fs-6">
           <a href="#" onClick={()=>signupPageClick()}>Sign up</a>
        </div>
    </div>
  );
}

export default Login;
