const rrr = require('react-router-redux');
const push = rrr.push()
const SUCCESS = 'SUCCESS'
const FAILURE = 'FAILURE'
const REQUEST ='REQUEST'
const initialState = {

    username : 'username ',
    password : 'password',
    success : 'false',
    file : ' <Login/>'
}
const loginreducer = (state = initialState,action)=>{
switch(action.type){
  case SUCCESS : console.log("success");return{
     

      username : action.user,
      password : action.pass,
      success : 'true'
    
  }
  case FAILURE : console.log("fail");return{
      ...state ,
      password : "failed"
  }

  case REQUEST :console.log("request");return{
    ...state ,
    username : "ACCESSED"
}
  case SIGNU :console.log("request");return{
    ...state ,
    username : "ACCESSED"
  }
  
  default : return state
}

}

export default loginreducer
