import React from 'react';
import {useSelector , useDispatch } from 'react-redux';
import Operations from '../redux/action';
import SignUpPage from '../redux/action'
import {useState } from 'react';
import Alert from 'react-popup-alert'

function Login() {
  const [ logindata,changedata] = useState({
      username : ' ',
      password : ' ',
      operation : 'signup'

  })
  function submitclick()
  {
      console.log(logindata.username);
    changedata({
      ...logindata ,
        operation : 'loginSubmit'
    })
    var userData = {
      'username' : logindata.username,
      'password' : logindata.password,
      'operation' : 'loginSubmit'
    }
    dispatch(Operations(userData));

  }
  function signupPageClick()
  {

    dispatch(Operations(logindata));

  }
  function handleChange(e)
  {
      console.log(e.target.value);
      console.log(logindata);
      if(e.target.id ==='x1')
      {
         changedata(
             {
            ...logindata ,
            username : e.target.value
             }
         ) ;


      }
      else if(e.target.id === 'x2')
      {
        changedata(
            {
           ...logindata ,
           password : e.target.value
            }
        ) ;
      }
  }
  const username = useSelector(state=>state.username);
  const password = useSelector(state=>state.password);
  const component = useSelector(state=>state.component);
  const alertMessage = useSelector(state=>state.alertMessage);
  const alertType = useSelector(state=>state.alertType);
  const alertShow = useSelector(state=>state.alertShow);
  console.log("alertMessage" , alertMessage)
  console.log(username,password , component);
  const dispatch = useDispatch()
  console.log("final" , username,password);
  return (

    <div className="container App">
       <h1>ENTER THE LOGIN DETAILS</h1>
       <div class = "row">
        <div class="col-lg-3">
           <h2>email</h2>
            <input id="x1"  text="USERNAME" onChange={handleChange} />
        </div>   

       </div>
       <div class = "row">
        <div class="col-lg-3">
        <h2>password</h2>
            <input id="x2" onChange={handleChange} />
        </div>   

       </div>
       <button onClick={()=>submitclick()}>SUBMIT</button>
       <button onClick={()=>signupPageClick()}>SIGNUP</button>
       <div class = "alertclass">
      <Alert
        header={'Header'}
        btnText={'Close'}
        text={alertMessage}
        type={alertType}
        show={alertShow}

        pressCloseOnOutsideClick={true}
        showBorderBottom={true}
        alertStyles={{}}
        headerStyles={{}}
        textStyles={{}}
        buttonStyles={{}}
      />
    </div>
    </div>

  );
}

export default Login;
